<script setup lang="ts">const {id} = useRoute().paramsconst uri = `https://fakestoreapi.com/products/${id}`;const {data: product} = await useFetch(uri, {key: id});if (!product.value) {  throw createError({statusCode: 404, statusMessage: 'Product not found', fatal: true})}definePageMeta({  layout: "products"})</script><template>  <div>    <ProductDetails :product="product"/>  </div></template><style scoped></style>